import { useState } from 'react'
import Vector from "../../assets/Vector.png"
import styled from 'styled-components'
import settings from '../../utils/styles/settings'

const StyleBox = styled.div`
background-color: ${settings.primary};
margin-top: 50px;
margin-left: 200px;
margin-bottom: 50px;
width: 70%;
height: 40px;
border-radius: 5px;
`
const StyleTitle = styled.span`
color: #ffffff;
font-family: ${settings.fontFamily};
margin-left: 20px`

const StyleVector = styled.img`
margin-left: 500px;
margin-top: 10px;`
const StyleContent = styled.div`
margin-left: 200px;
width: 70%;
`
function Dropdown({ title, children}) {
  const [isOpen, setIsOpen] = useState(false)
  const rotate = isOpen ? 'rotate(180deg)' : 'rotate(0)'
  const rotation = { transform: rotate, transition: '0.6s ease-in-out' }

  return (
    <div>
      <StyleBox onClick={(e) => {setIsOpen(!isOpen)}}>
        <StyleTitle>{title}</StyleTitle>
        <StyleVector style={rotation} src= { Vector } alt="dropdown"/>
      </StyleBox>
      {isOpen && (
        <StyleContent>
          {children}
        </StyleContent>
      )}
    </div>
  )
}

export default Dropdown
