import { useState } from 'react'
import Vector from "../../assets/Vector.png"
import styled from 'styled-components'
import settings from '../../utils/styles/settings'
const StyleTitle = styled.div`
diplay: flex;
flex-direction: row;
justify-content: space-between;
background-color: ${settings.primary};
margin-top: 50px;
margin-left: 200px;
width: 70%;
height: 30px;
color: white;
border-radius: 5px;
`
const StyleContent = styled.div`
margin-left: 200px;
width: 70%;
`
function Dropdown({ title, children}) {
  const [isOpen, setIsOpen] = useState(false)
  const rotate = isOpen ? 'rotate(180deg)' : 'rotate(0)'
  const rotation = { transform: rotate, transition: '0.6s ease-in-out' }

  return (
    <div className="dropdown">
      <StyleTitle onClick={(e) => {setIsOpen(!isOpen)}}>
        <span>{title}</span>
        <img style={rotation} src= { Vector } alt="dropdown"/>
      </StyleTitle>
      {isOpen && (
        <StyleContent>
          {children}
        </StyleContent>
      )}
    </div>
  )
}

export default Dropdown
